customCSS:
.forceTopicFeaturedTopics {
    font-family: Montserrat;
    font-size: xx-large;
}

.faqcontainer {
  position: relative;
  text-align: center;
  color: white;
  font-family: Montserrat;
  font-size: xx-large;
}

.faqcentered {
  position: absolute;
  top: 50%;
  left: 50%;
  transform: translate(-50%, -50%);
}
.alertbanner {
  padding: 10px;
  background-color: #03b0ed;
  color: white;
  opacity: 1;
  transition: opacity 0.6s;
  margin-bottom: 15px;
  text-decoration:none;
  font-size: 20px;
}

@font-face {
    font-family: 'gotham_htf';
    src: url('https://www.legendboats.com/wp-content/themes/legendboats/build/fonts/GothamHTF/gothamhtf-light-webfont.woff') format('woff'),;
    font-weight: 300;
    font-style: normal;
    font-display: swap;
}
@font-face {
    font-family: 'gotham_htf';
    src: url('https://www.legendboats.com/wp-content/themes/legendboats/build/fonts/GothamHTF/gothamhtf-medium-webfont.woff') format('woff');
    font-weight: 600;
    font-style: normal;
    font-display: swap;
}
@font-face {
    font-family: 'gotham_htf';
    src: url('https://www.legendboats.com/wp-content/themes/legendboats/build/fonts/GothamHTF/gothamhtf-black-webfont.woff') format('woff');
    font-weight: 900;
    font-style: normal;
    font-display: swap;
}
.howto-wrap .ro{
  position: absolute;
  overflow: hidden;
  clip: rect(0 0 0 0);
  height: 1px; width: 1px;
  margin: -1px; padding: 0; border: 0;
}
.howto-wrap {
  width: 100vw;
  background-color: #f5f5f5;
  margin-left: calc(50% - 50vw);
  margin-right: calc(50% - 50vw);
    padding-bottom: 2em;
}
.howto{
    display: block;
    width: 95%; max-width: 1080px;
    max-width: 1645px;
    margin: 0 auto;
}
.howto-wrap .howto-list {
  display: flex;
  justify-content: space-between;
  flex-wrap: wrap;
  margin: 0;
  padding: 0;
}
.howto-wrap .howto-list .howto-option {
  list-style: none;
  width: 48%;
  padding: 1%;
  box-sizing: border-box;
  background-color: #FFF;
  margin: 2em 0 0;
}
.howto-wrap .howto-list .howto-option.chat{
  width: 100%;
  background-color: transparent;
  border: 0 none;
}
.howto-wrap .howto-list .or{
    width: 80%;
    text-align: center;
    border-top: 1px solid #0d506d;
    font-size: 2em;
    font-weight: 900;
    text-transform: uppercase;
    margin: 2em auto 0;
}
.howto-wrap .howto-list .or span{
    display: block;
    background-color: #f5f5f5;
    padding: 0 0.5em;
    margin: -0.8em auto 0;
    width: fit-content;
}
.howto-wrap .howto-list .howto-option .details {
  display: grid;
  width: 100%;
}
.howto-wrap .howto-list .howto-option .details h4 {
  font-size: 1.75em;
  padding-bottom: 0.5em;
  grid-column-start: 1;
  grid-column-end: 3;
  grid-row-start: 1;
  grid-row-end: 3;
  align-self: center;
}
.howto-wrap .howto-list .howto-option.chat .details h4 {
  font-size: 1.75em;
  padding-bottom: 0.5em;
  grid-column-start: 1;
  grid-column-end: 2;
  grid-row-start: 1;
  grid-row-end: 3;
  align-self: center;
  text-align: right;
}
.howto-wrap .howto-list .howto-option .details h4 strong {
  font-weight: 900;
  color: #0d506d;
}
.howto-wrap .howto-list .howto-option .details h4 span {
  display: block;
}
.howto-wrap .howto-list .howto-option .details .cta {
  display: block;
  grid-column-start: 1;
  grid-column-end: 4;
  grid-row-start: 3;
  grid-row-end: 4;
}
.howto-wrap .howto-list .howto-option.chat .details .cta {
  display: block;
  justify-content: center;
  align-items: center;
  text-align: center;
  grid-column-start: 1;
  grid-column-end: 3;
  grid-row-start: 3;
  grid-row-end: 3;
}
.howto-wrap .howto-list .howto-option.chat .details .cta strong{
  display: inline-block;
    background: #0d506d;
    font-weight: 900;
    font-size: 1.5em;
    padding: 0 0.15em;
    border-radius: 100vh;
    color: #FFF;
    width: 30px;
    margin: 0 0.25em;
    height: 30px;
}
.howto-wrap .howto-list .howto-option .details .icon {
  display: block;
  margin-left: 1em;
  grid-column-start: 3;
  grid-column-end: 4;
  grid-row-start: 1;
  grid-row-end: 3;
}
.howto-wrap .howto-list .howto-option.chat .details .icon {
  display: block;
  margin-left: 1em;
  grid-column-start: 2;
  grid-column-end: 3;
  grid-row-start: 1;
  grid-row-end: 3;
}
.howto-wrap .howto-list .howto-option.chat .details .icon {
  width: 180px;
}
.howto-wrap .howto-list .howto-option.quote .details .icon {
  width: 130px;
}
.howto-wrap .howto-list .howto-option.chat svg, .howto-wrap .howto-list .howto-option.quote svg {
  width: 100%;
  height: auto;
  display: block;
}
.howto-wrap .howto-list .howto-option.chat svg path, .howto-wrap .howto-list .howto-option.quote svg path {
  fill: #0d506d;
}
.howto-wrap .howto-list .howto-option.howto-option--large {
  display: block;
  width: 100vw;
  margin-top: 0;
  margin-bottom: 0;
  padding: 0;
  box-sizing: content-box;
  margin-left: calc(50% - 50vw);
  margin-right: calc(50% - 50vw);
  padding-bottom: 56%;
  background-image: url("https://www.legendboats.com/wp-content/themes/legendboats/build/img/standard/no-boat-show/showroom-background.png");
  background-size: contain;
  background-position: bottom center;
  background-repeat: no-repeat;
}
.howto-wrap .howto-list .howto-option.howto-option--large h4 {
  margin-bottom: 0;
}
.howto-wrap .howto-list .howto-option.howto-option--large p {
  max-width: 500px;
  margin-bottom: 1.5em;
}
.howto-wrap .howto-list .howto-option.howto-option--large div {
  padding: 2.5%;
  box-sizing: border-box;
}
.howto-wrap .covid-protocols {
  position: relative;
  padding: 4vw 0;
}
.howto-wrap .covid-protocols::before {
  content: "";
  display: block;
  position: absolute;
  top: 0;
  left: 0;
  width: 100vw;
  height: 100%;
  margin-left: calc(50% - 50vw);
  margin-right: calc(50% - 50vw);
  background: white;
  background: -webkit-linear-gradient(white 0%, #e1e1e1 43%, #e1e1e1 100%);
  background: -o-linear-gradient(white 0%, #e1e1e1 43%, #e1e1e1 100%);
  background: linear-gradient(white 0%, #e1e1e1 43%, #e1e1e1 100%);
  z-index: 1;
}
.howto-wrap .covid-protocols ul {
  display: flex;
  justify-content: center;
  position: relative;
  z-index: 2;
  margin: 0;
  padding: 0;
}
.howto-wrap .covid-protocols ul li {
  list-style: none;
  text-align: center;
  width: 25%;
  padding: 0 2.5%;
}
.howto-wrap .covid-protocols ul li .icon {
  border: 2px solid #0d506d;
  border-radius: 100vh;
  display: block;
  max-width: 160px;
  margin: 0 auto 0.5em;
}
.howto-wrap .covid-protocols ul li .icon svg {
  width: 60%;
  height: auto;
  margin: 20% 0;
}
.howto-wrap .covid-protocols ul li .icon svg path {
  fill: #0d506d;
}
.howto-wrap .promoLeadFooter {
  padding: 3em 0;
  width: 100vw;
  background: #FFF;
  margin-left: calc(50% - 50vw);
  margin-right: calc(50% - 50vw);
}
.howto-wrap .promoLeadFooter header h4 {
  font-family: "gotham_htf" !important;
  font-weight: 300;
  font-size: 2.5em;
}
.howto-wrap .promoLeadFooter header p {
  font-family: "gotham_htf" !important;
  font-weight: 900;
  font-size: 2em;
  margin: 0 auto;
  color: #0d506d;
}
.howto-wrap .promoLeadFooter .boatTypes {
  width: 95%;
  max-width: 1068px;
  margin: 2.5em auto;
  padding: 0;
  display: flex;
  justify-content: space-between;
}
.howto-wrap .promoLeadFooter .boatTypes .typeCard {
  width: 48%;
  margin-right: 2%;
  background-color: #FFF;
  display: flex;
  flex-direction: column;
}
.howto-wrap .promoLeadFooter .boatTypes .typeCard span {
  display: block;
  width: 100%;
}
.howto-wrap .promoLeadFooter .boatTypes .typeCard span img {
  width: 100%;
}
.howto-wrap .promoLeadFooter .boatTypes .typeCard div {
  display: flex;
  flex-direction: column;
  flex-grow: 1;
  padding: 5%;
  box-sizing: border-box;
}
.howto-wrap .promoLeadFooter .boatTypes .typeCard div p {
  font-size: 1em;
  font-family: "gotham_htf" !important;
  font-weight: 300;
  text-align: left;
  line-height: 1.5;
}
.howto-wrap .promoLeadFooter .boatTypes .typeCard a {
  display: inline-block;
  width: fit-content;
  width: -moz-fit-content;
  margin: 0 5% 5%;
  border: 2px solid #007ba8;
  border-radius: 8px;
  color: #007ba8;
  padding: 0.5em 1.5em;
  font-size: 1.25em;
  font-family: "gotham_htf";
  font-weight: 600;
  transition: all 0.2s;
}
.howto-wrap .promoLeadFooter .boatTypes .typeCard a:hover, .howto-wrap .promoLeadFooter .boatTypes .typeCard a:focus {
  background: #007ba8;
  color: #FFF;
  text-decoration: none;
}
.howto-wrap .promoLeadFooter .boatTypes .typeCard:last-of-type {
  margin-right: 0;
}

.howto-wrap .btn {
  display: flex;
  align-items: center;
  transition: all 0.3s linear;
  border: 1px solid #0d506d;
  border-radius: 5px;
  text-decoration: none;
  width: fit-content;
  width: -moz-fit-content;
  cursor: pointer;
  font-size: 1.25em !important;
  font-weight: 900 !important;
  line-height: 1;
  box-sizing: border-box;
  padding: 0.5em 1em;
}
.howto-wrap .btn span {
  display: block;
}
.howto-wrap .btn span.icon {
  margin-right: 0.75em;
  height: 1.5em;
  width: 1.5em;
}
.howto-wrap .btn span.icon svg {
  height: 100%;
  transition: all 0.2s linear;
}
.howto-wrap .btn span.icon svg path {
  fill: rgba(255, 255, 255, 0.6) !important;
}
.howto-wrap .btn:hover span.icon svg path, .howto-wrap .btn:focus span.icon svg path {
  fill: #0d506d !important;
}
.howto-wrap .btn.btn--primary {
  background-color: #0d506d;
  color: #FFF;
}
.howto-wrap .btn.btn--primary:hover, .howto-wrap .btn.btn--primary:focus {
  background-color: #FFF;
  color: #0d506d;
  text-decoration: none;
}

.mercury{
    display: block;
    width: 75%; max-width: 350px;
    margin: 2em auto 0;
}

@media screen and (min-width: 1130px) {
  .howto-wrap .howto-list .howto-option h4 {
    font-size: 1.75em;
  }
}
@media screen and (max-width: 995px) {
  .howto-wrap .howto-list .howto-option .details {
    row-gap: 10px;
  }
  .howto-wrap .howto-list .howto-option .details h4 {
    grid-row-end: 3;
    align-self: center;
  }
  .howto-wrap .howto-list .howto-option .details .cta {
    grid-column-start: 1;
    grid-column-end: 4;
    grid-row-start: 3;
    grid-row-end: 4;
  }
}


@media screen and (max-width: 700px) {
  .howto-wrap .howto-list .howto-option {
    width: 100%;
    padding: 2.5%;
    border: 0 none;
  }  
    .howto-wrap .howto-list .howto-option.chat{
        background-color: #FFF;
    }
    .howto-wrap .howto-list .howto-option.last::after, .howto-wrap .howto-list .howto-option.chat::after{
        display: none;
    }

  .howto-wrap .howto-list .howto-option:first-of-type {
    margin-bottom: 0;
  }
  .howto-wrap .howto-list .howto-option .details h4 {
    grid-row-end: 2;
    align-self: center;
  }
  .howto-wrap .howto-list .howto-option .details .cta {
    grid-column-start: 1;
    grid-column-end: 3;
    grid-row-start: 2;
    grid-row-end: 3;
  }
}
@media screen and (max-width: 480px) {
  .howto-wrap .howto-list .howto-option .details h4 {
    grid-row-end: 3;
    align-self: center;
  }
  .howto-wrap .howto-list .howto-option .details .cta {
    grid-column-start: 1;
    grid-column-end: 4;
    grid-row-start: 3;
    grid-row-end: 4;
  }
}
